import { Divider } from '@mui/material'
import React, { useEffect } from 'react'
import { formatISO9075 } from "date-fns";

const Post = (props) => {

    useEffect(() => {
        console.log(props)
    }, [])

    return (
        <div className='post'>
            <div className='img'>
                {props.picture && (
                    <img className='img' src={props.picture} />
                )}

                {!props.picture && (
                    <div className='rectangle green regular-font'>
                        No Image Provided
                    </div>
                )}
            </div>
            <div className='post-content'>
                <h2 className='white bold-font'>{props.title}<span className='green bold-font'>.</span></h2>

            </div>
            <div>
                {/* <h4 className='thin-font date-time green'>{formatISO9075(props.createdAt)}</h4> */}
            </div>

            <p className='thin-font white explanation'>{props.explanation}</p>
            <h4 className='thin-font email'><span className='date'>Reported By:</span> <span className='green space'><h3>{props.email}</h3></span></h4>
        </div>
    )
}

export default Post
